--
-- Cấu trúc cho view `viewcate`
--
DROP TABLE IF EXISTS `viewcate`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `viewcate`  AS SELECT `category`.`id` AS `id`, `category`.`name` AS `name` FROM (`category` join `viewnumproductcate`) WHERE `category`.`parent_id` = 0 GROUP BY `category`.`id``id`  ;

-- --------------------------------------------------------

--
-- Cấu trúc cho view `viewnumproductcate`
--
DROP TABLE IF EXISTS `viewnumproductcate`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `viewnumproductcate`  AS SELECT `category`.`parent_id` AS `parent_id`, count(`product`.`id`) AS `numProduct` FROM ((`product` join `list_cate_product`) join `category`) WHERE `product`.`id` = `list_cate_product`.`id_product` AND `list_cate_product`.`id_cate` = `category`.`id` GROUP BY `category`.`parent_id``parent_id`  ;

